mapper.delete:
delete from treasure
where id = #{id,jdbcType=INTEGER}
  and userID = #{userid,jdbcType=INTEGER}
  and treasureType = #{treasuretype,jdbcType=INTEGER}

mapper.update:
update treasure
set treasureName = #{treasurename,jdbcType=VARCHAR},
  treasureValue = #{treasurevalue,jdbcType=INTEGER}
where id = #{id,jdbcType=INTEGER}
  and userID = #{userid,jdbcType=INTEGER}
  and treasureType = #{treasuretype,jdbcType=INTEGER}

mapper.select:
select id, userID, treasureType, treasureName, treasureValue
from treasure
where id = #{id,jdbcType=INTEGER}
  and userID = #{userid,jdbcType=INTEGER}
  and treasureType = #{treasuretype,jdbcType=INTEGER}
